import sobe from '../images/icon-sobe.png'
import desce from '../images/icon-desce.png'

export const Informacoes = (props) => {
    const handleSobeClick = () => {
        const container = document.querySelector('.container-informacoes');
        container.classList.remove('desce');
        container.classList.add('sobe');
    };

    const handleDesceClick = () => {
        const container = document.querySelector('.container-informacoes');
        container.classList.remove('sobe');
        container.classList.add('desce');
    };

    return (
        <div className="container-informacoes">
            <h3>{props.titulo}</h3>
            {props.texto}

            <div className="navegacao">
              <img className='navegacao-img sobe' src={sobe} onClick={handleSobeClick} />
              <img className='navegacao-img desce' src={desce} onClick={handleDesceClick} />
            </div> 
        </div>
    )
}


