import React, { useState, useRef, useEffect } from 'react'
import "./style.css"
import perfil from '../images/euhelena.jpg'
import email from '../images/icon-email.png'
import github from '../images/icon-github.png'
import linkedin from '../images/icon-linkedin.png'
import sobe from '../images/icon-sobe.png'
import desce from '../images/icon-desce.png'
import sobeTudo from '../images/icon-sobe-tudo.png'
import desceTudo from '../images/icon-desce-tudo.png'
import { Informacoes } from './Informacoes'

export const Perfil = () => {
    const [posicao, setPosicao] = useState(0);
    const [canSubirTudo, setCanSubirTudo] = useState(false)
    const [canDescerTudo, setCanDescerTudo] = useState(false)
    const animationInformacoes = useRef(null)

    useEffect(() => {
        if (posicao === 0) {
            setCanDescerTudo(true)
        } else {
            setCanDescerTudo(false)
        }

        const numInformacoes = animationInformacoes.current.childElementCount
        if ( posicao == numInformacoes*-100 ) {
            setCanSubirTudo(true)
        }
    },[])

    const handleDesce = () => {
        setPosicao(posicao - 100)
        if( posicao < 0 )setCanDescerTudo(false)
    }

    const handleSobe = () => {
        setPosicao(prevPosicao => prevPosicao + 100)
        setCanDescerTudo(true) 
    }

    const posicaoStyle = {
        transform: `translateY(${posicao}vh)`
    }

  return (
    <div className="container">
      <div className="container-perfil">
        <img className="perfil" src={perfil} alt="Minha imagem de perfil" />
        <h1 className="nome">Helena Maia</h1>
        <h2 className="cargo">Desenvolvedora Front-End</h2>
        <button className="contato">Fale Comigo!</button>
        <div className="icons">
              <img className='icon-img' src={email} href="mailto:helenaaraujo.dev@gmail.com"/>
            <img className='icon-img' src={github}/>
            <img className='icon-img' src={linkedin}/>
        </div>
      </div>

      <div className="animation-informacoes" style={posicaoStyle} ref={animationInformacoes}>
        <Informacoes titulo="Sobre Mim" texto={sobreMim()} />
        <Informacoes titulo="Soft Skills" texto={softSkills()} />
        <Informacoes titulo="Hard Skills" texto={hardSkills()} />
        <Informacoes titulo="Experiência" texto={experiencia()} />
      </div>  

      <div className="navegacao">

        {canDescerTudo ? <img className='navegacao-img' src={desceTudo}/> : <img className='navegacao-img' src={sobe} onClick={handleSobe} />}
        
        {canSubirTudo ? <img className='navegacao-img' src={sobeTudo}/> : <img className='navegacao-img' src={desce} onClick={handleDesce} />}
      </div> 
    </div>
    )
}

const sobreMim = () => {
    return (
        <p>
            Sou uma desenvolvedora front-end apaixonada por tecnologia, atualmente cursando Análise e Desenvolvimento de Sistemas e trabalhando no Ministério Público de Rondônia. Estou em constante busca por aprimoramento e aprendizado, sempre em sintonia com as últimas tendências do mercado.
            <br/>
            <br/>
            Minha paixão pela tecnologia me motiva a enfrentar desafios e resolver problemas com lógica e código. Além disso, estou comprometida em contribuir para projetos inovadores como parte de uma equipe dedicada e ansiosa por oportunidades empolgantes no mundo em constante evolução da tecnologia.
        </p>
    )
}

const softSkills = () => {
