import React, {useState} from 'react'
import sobe from '../images/icon-sobe.png'
import desce from '../images/icon-desce.png'

export const Informacoes = (props) => {
    const [posicao, setPosicao] = useState('transform: translateY(0vh);')
    
    const handleSobe = ()=>{
        setPosicao({transform: "translateY(-100vh)"})
    }
    const handleDesce = ()=>{}

    return (
        <div className="container-informacoes" style={posicao}>
            <h3>{props.titulo}</h3>
            {props.texto}

            <div className="navegacao">
              <img className='navegacao-img sobe' src={sobe} onClick={handleSobe}/>
              <img className='navegacao-img desce' src={desce} onClick={handleDesce} />
            </div> 
        </div>
    )
}


